import React, { useEffect, useState } from 'react';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

// Componente: Bloco isolado o qual não interfere no restante da aplicação. É uma função que retorna um conteúdo HTML, CSS ou JS
// Propriedade: São os atributos de um componente. Ex.: title, id, className. Informações que um componente PAI passa para o componente FILHO
// Estado: Informações mantidas pelo componente (Lembrar do conceito imutabilidade). {useState}

function App() {
  const [github_username, setGithubUsername] = useState('');
  const [techs, setTechs] = useState('');

  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');


  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {

        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);

      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    );
  }, []);

  return (
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form>
          <div className="input-block">
            <label htmlFor="github_username">Usuário do Github</label>
            <input
              id="github_username"
              name="github_username"
              required
              value={github_username}
              onChange={e => setGithubUsername(e.target.value)}
            ></input>
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias</label>
            <input
              id="techs"
              name="techs"
              required
              value={techs}
              onChange={e => setTechs(e.target.value)}
            ></input>
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="Latitude">Latitude</label>
              <input
                id="Latitude"
                type="number"
                name="Latitude"
                required
                value={latitude}
                onChange={e => setLatitude(e.target.value)}
              ></input>
            </div>

            <div className="input-block">
              <label htmlFor="Longitude">Longitude</label>
              <input
                id="Longitude"
                type="number"
                name="Longitude"
                required
                value={longitude}
                onChange={e => setLongitude(e.target.value)}
              ></input>
            </div>
          </div>

          <button type="submit">Salvar </button>

        </form>
      </aside>

      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img src="https://avatars3.githubusercontent.com/u/37714181?s=460&v=4" alt="Felipe Hoffmann" />
              <div className="user-info">
                <strong>Felipe Hoffmann</strong>
                <span>ReactJS, React Native, Node.s</span>
              </div>
            </header>
            <p>Estudante da Omnistack</p>
            <a href="https://github.com/felipejh">Acessar perfil no Github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars3.githubusercontent.com/u/37714181?s=460&v=4" alt="Felipe Hoffmann" />
              <div className="user-info">
                <strong>Felipe Hoffmann</strong>
                <span>ReactJS, React Native, Node.s</span>
              </div>
            </header>
            <p>Estudante da Omnistack</p>
            <a href="https://github.com/felipejh">Acessar perfil no Github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars3.githubusercontent.com/u/37714181?s=460&v=4" alt="Felipe Hoffmann" />
              <div className="user-info">
                <strong>Felipe Hoffmann</strong>
                <span>ReactJS, React Native, Node.s</span>
              </div>
            </header>
            <p>Estudante da Omnistack</p>
            <a href="https://github.com/felipejh">Acessar perfil no Github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars3.githubusercontent.com/u/37714181?s=460&v=4" alt="Felipe Hoffmann" />
              <div className="user-info">
                <strong>Felipe Hoffmann</strong>
                <span>ReactJS, React Native, Node.s</span>
              </div>
            </header>
            <p>Estudante da Omnistack</p>
            <a href="https://github.com/felipejh">Acessar perfil no Github</a>
          </li>
        </ul>
      </main>

    </div>
  );
}

export default App;
